import React,{useState} from 'react'

export default function TextForm(props) {
    const handleUpClick=()=>{
        let newtext=text.toUpperCase();
        setText(newtext);
    }
    const handleDoClick=()=>{
        let newtext=text.toLowerCase();
        setText(newtext);
    }
    const handleOnChange=(event)=>{
        setText(event.target.value);
    }
    const handleClear=()=>{
        let newText='';
        setText(newText);
    }
    const handleCopy=()=>{
        var text=document.getElementById("myBox");
        text.select();
        navigator.clipboard.writeText(text.value);
    }
    const handleExtraSpace=()=>{
        let newText=text.split(/[ ]+/);
        setText(newText.join(" "));
    }
   
    const handleCase=()=>{
        const words = text.split(" ");
        for (let i = 0; i < words.length; i++) {
            words[i] = words[i][0].toUpperCase() + words[i].substr(1);
        }
        setText(words.join(" "));
    }
    const [text,setText]=useState('');
  return (
    <>
    <div style={{color:props.mode==='dark'?'white':'black'}}>
        <h1 className='container mx-5'>{props.heading}</h1>
        <div className="mb-3">
        <textarea className="form-control mx-5" onChange={handleOnChange} value={text} style={{backgroundColor:props.mode==='dark'?'grey':'white',color:props.mode==='dark'?'white':'black'}} id="myBox" rows="8"></textarea>
    </div>
    <button className="btn btn-primary mx-3 my-2" onClick={handleUpClick}>Convert to uppercase</button>
    <button className="btn btn-primary mx-2 my-2" onClick={handleDoClick}>Convert to uppercase</button>
    <button className="btn btn-primary mx-2 my-2" onClick={handleClear}>Clear Text</button>
    <button className="btn btn-primary mx-2 my-2" onClick={handleCopy}>Copy Text</button>
    <button className="btn btn-primary mx-2 my-2" onClick={handleExtraSpace}>Remove Extra Spaces</button>
    <button className="btn btn-primary mx-2 my-2" onClick={handleCase}>First letter to uppercase</button>
    
    </div>
    <div className='container my-4 mx-5' style={{color:props.mode==='dark'?'white':'black'}}>
        <h1>Your text summary</h1>
        <p>{text.split(" ").length} words {text.length} characters</p>
        <p>{0.008*text.split(" ").length} Minutes Read</p>
        <h2>Preview</h2>
        <p>{text}</p>
    </div>
    </>
  )
}
